package DataTypesandVariables;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//primitive datatypes
		int x = 10;
		int y = 20;
		
		System.out.print("The sum of x, y is " + (x+y));
		
		short sh = 127;
		byte bt = 90;
		
		System.out.print("\nThe Short and Byte values are " +sh+ ", "+bt);
		
		float f = 15.24F;
		double d = 1234.1234;
		long l = 123456789L;
		
		System.out.print("\nThe float, long and double values are " +f+ ", " +d+ ", " +l);
		
		char ch = 's';
		boolean bl = true;
		
		System.out.print("\nThe char and boolean values are " +ch+ ", "+bl);
		
		
		//Non-Primitive Data Type
		String string = "Gowri Shankar";
		
		System.out.print("\nThe String is " +string);

		
	}
}
